<template>  <div class="not-found">    <h1>404</h1>    <p>Strona, której szukasz, nie istnieje - 404</p>    <button @click="goHome">Powrót do strony głównej</button>  </div></template><script>import { useRouter } from 'vue-router'export default {  name: 'NotFound',  setup() {    const router = useRouter()    const goHome = () => {      router.push('/')    }    return {      goHome    }  }}</script>