<template>  <div class="unauthorized">    <h1>Brak dostępu</h1>    <p>Nie masz uprawnień do wyświetlenia tej strony - 403</p>    <button @click="goHome">Powrót do strony głównej</button>  </div></template><script>import { useRouter } from 'vue-router'export default {  name: 'Unauthorized',  setup() {    const router = useRouter()    const goHome = () => {      router.push('/home')    }    return {      goHome    }  }}</script>